import React, { useState, useEffect } from "react";
import FoodForm from "../../components/FoodForm";
import {
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Typography
} from "@mui/material/";


import './styles.css'
// import { makeStyles } from "@mui/styles";
import DisplayFood from "../../components/DisplayFood";
import EditFood from "../../components/EditFood";

import AccordionTable from "../../components/AccordionTable";


function AddFood() {
  const [dummy, setDummy] = useState(false)
  const [isEdit, setIsEdit] = useState(false)
  const [editFood, setEditFood] = useState({})
  const [loading, setLoading] = useState(true);

//   useEffect(() => {
//     if (loading) {
//       window.location.reload();
//     }
// }, [loading])


  // const useStyles = makeStyles({
  //   root: {
  //     // '&:MuiSvgIcon-root': {
  //     //   flex: 1,
  //     //   // paddingLeft:"90vh"
  //     // },
  //     width: "50%",
  //     margin: "0px auto",
  //   },
  // });
  // const classes = useStyles();

  return (
    <div id="backImage" style={{ height: "100vh" }}>

      <div >
      <AccordionTable />
        <Accordion  style={{ paddingLeft: "10px" }} >
          <AccordionSummary

            aria-controls="panel1a-content"
            id="panel1a-header"
          >
            <Typography>Breakfast</Typography>
          </AccordionSummary>
          <AccordionDetails>
            <FoodForm setDummy={setDummy} dummy={dummy} />
          </AccordionDetails>
        </Accordion>

        <Accordion style={{ paddingLeft: "10px" }}>
          <AccordionSummary

            aria-controls="panel2a-content"
            id="panel2a-header"
          >
            <Typography>Lunch</Typography>
          </AccordionSummary>
          <AccordionDetails>
            <FoodForm setDummy={setDummy} dummy={dummy} />
          </AccordionDetails>
        </Accordion>

        <Accordion  style={{ paddingLeft: "10px" }}>
          <AccordionSummary

            aria-controls="panel3a-content"
            id="panel3a-header"
          >
            <Typography>Dinner</Typography>
          </AccordionSummary>
          <AccordionDetails>
            <FoodForm setDummy={setDummy} dummy={dummy} />
          </AccordionDetails>
        </Accordion>

        <DisplayFood setDummy={setDummy} dummy={dummy} setIsEdit={setIsEdit} setEditFood={setEditFood} />
        {isEdit ? <EditFood
          open={isEdit}
          setIsEdit={setIsEdit}
          existingFood={editFood}
        /> : false
        }
      </div>
    </div>
  )

}

export default AddFood;
